import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";
import Logo from "../../assets/logo.svg";
import { RegisterRoute } from "../../util/ApiRoutes";

const Register = () => {

  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  })

  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover:true,
    draggable:true,
    theme: "dark",
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(handleValidation()){
      const {username, email, password, confirmPassword} = values;
      const {data} = await axios.post(RegisterRoute)
    }
  };

  const handleValidation = () => {
    const { username, email, password, confirmPassword } = values;
    if (username && email && password && confirmPassword) {
      if (password !== confirmPassword) {
        toast.error("Passwords do not match", toastOptions);
        return false;
      } else if (username.length < 3) {
        toast.error("Username must be at least 3 characters", toastOptions);
        return false;
      } else if (password.length < 6) {
        toast.error("Password must be at least 6 characters", toastOptions);
        return false;
      } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(email)) {
        toast.error("Invalid email address", toastOptions);
        return false;
      }
    }
  }

  const handleChange = (e) => {
    setValues({...values, [e.target.name]: e.target.value});
  };

  useEffect(()=>{

  })

  return (
    <>
    <FormContainer>
      <form onSubmit={(e) => handleSubmit(e)}>
        <div className="brand">
          <img src={Logo} alt="logo" />
          <h1>snappy</h1>
        </div>
        <input
          type="text"
          placeholder="Username"
          name="username"
          onChange={(e) => handleChange(e)}
        />
        <input
          type="email"
          placeholder="Email"
          name="email"
          onChange={(e) => handleChange(e)}
        />
        <input
          type="password"
          placeholder="Password"
          name="password"
          onChange={(e) => handleChange(e)}
        />
        <input
          type="password"
          placeholder="Confirm Password"
          name="confirmPassword"
          onChange={(e) => handleChange(e)}
        />
        <button type="submit">Create User</button>
        <span>Already have an acount? <Link to="/login">Login</Link></span>
      </form>
    </FormContainer>
    <ToastContainer />
    </>
  );
};

const FormContainer = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    background-color: #131324;
    .brand{
        display: flex;
        align-items: center;
        gap: 1rem;
        justify-content: center;
        img{
            height: 5rem;
        }
        h1{
            color:#fff;
            text-transform: uppercase;
        }
    }
    form{
        display: flex;
        flex-direction: column;
        gap: 2rem;
        background-color: #00000076;
        border-radius: 2rem;
        padding: 3rem 5rem;
        input{
            background-color: transparent;
            padding: 1rem;
            border: 0.1rem solid #4e0eff;
            border-radius: 0.4rem;
            color: #ffff;
            width: 100%;
            font-size: 1rem;
            &:focus{
                border: 0.1rem solid #997af0;
                outline: none;
            }
        }
        button{
            background-color: #997af0;
            color: #fff;
            padding: 1rem 2rem;
            border: none;
            font-weight: bold;
            cursor: pointer;
            border-radius: 0.4rem;
            font-size: 1rem;
            text-transform: uppercase;
            transition: 0.5s ease-in-out;
            &:hover{
                background-color: #4e0eff;
            }
        }
        span{
            color: #fff;
            text-transform: uppercase;
            a{
                color: #4e0eff;
                text-transform: none;
                text-decoration: none;
                font-weight: bold;
            }
        }
    }

`;

export default Register;
